import React, { Component } from 'react';
import axios from 'axios';
// import UserCard from './components/userCard'

class App extends Component{
  constructor(){
    super()
    this.state = {
      users: [{
        username: 'dangnlu18',
        avatar_url: 'https://avatars0.githubusercontent.com/u/38761634?v=4',
        following: 3,
        following_url: "https://api.github.com/users/dangnlu18/following{/other_user}",
        followers: 3,
        followers_url:"https://api.github.com/users/dangnlu18/followers"
      }]
    }
  }

  componentDidMount(){
    this.fetchUsers()

  }

  componentDidUpdate(prevProps, prevState){
    if (prevState.user !== this.state.user){
      this.setState({
        connections: []
      })
      console.log(this.state.connections)
      this.fetchUsers()
    }
  }


  fetchUsers = () => {

    axios.get(`https://api.github.com/users/${this.state.username}`)
    .then(resp=>{
      console.log(resp)
      this.setState({
      connections: resp.data
      })
    })
    .catch(err =>{
      console.log('error: ', err)
    })

  }

  handleSubmit = (event) =>{
    event.preventDefault()

    this.setStatus({
      user: ''
    })
  }

  render(){
    return (
      <div className="App">
        <h1> GitHub User Cards </h1>
          <form onSubmit={this.handleSubmit}>
            <input placeholder="enter in user name" type="text" />
            <button> Add </button>
            </form>
          <p> {this.state.user.username} </p>
      </div>
    );
  }
}

export default App;
