import React, { Component } from 'react';
import axios from 'axios';
// import UserCard from './components/userCard'

class App extends Component{
  constructor(){
    super()
    this.state = {
      user: 'dangnlu18',
      connections: []
    }
  }

  componentDidMount(){
    this.fetchUsers()
  }

  componentDidUpdate(){
    this.fetchUsers()
  }


  fetchUsers = () => {
    axios.get(`https://api.github.com/users/${this.state.user}`)
    .then(resp=>{
      this.setState({
      connections: resp.data
      })
    })
    .catch(err =>{
      console.log('error: ', err)
    })

  }

  handleChange = (event) =>{
    this.setStatus({
      user: event.target.value
    })
  }

  render(){
    console.log(thisconnections)
    return (
      <div className="App">
        <h1> GitHub User Cards </h1>
          <input placeholder="enter in user name" type="text" />
          <button onSubmit={this.handleChange}> Add </button>
          <p> {this.state.user} </p>
      </div>
    );
  }
}

export default App;
